(function(){
  // Minimal shim to avoid runtime errors if offline file isn't yet downloaded.
  if(!window.pinyinPro){
    window.pinyinPro = {
      pinyin: function(text, opts){
        // If input contains Chinese, return simple pinyin without tone to allow downstream detection to fail and fallback to CDN
        const hasHan = /[\u4e00-\u9fa5]/.test(String(text||''));
        if(hasHan){
          return (opts && opts.type==='array') ? [''] : '';
        }
        const words = String(text||'').split(/\s+/);
        return (opts && opts.type==='array') ? words : words.join(' ');
      }
    };
  }
})();


